import { Component, Input, OnInit, EventEmitter, Output } from '@angular/core';
import { Router, ActivatedRoute, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-lancamentos',
  templateUrl: './lancamentos.component.html',
  styleUrls: ['./lancamentos.component.css']
})
export class LancamentosComponent implements OnInit {
  tiposLancamento = ["Ativo", "Passivo", "Ativo não Circulante", "Passivo não Circulante", "Patrimônio Líquido"];
  contas = ["Caixa", "Banco", "Imobilizado", "Estoque", "Fornecedor", "Empréstimo", "Capital Social"];
  tipoLancamento: string = '';
  conta: string = '';
  valor: number = 0;
  nome: string = '';
  descricao: string = '';
  tipoLancamentoContrapartida: string = '';
  contrapartidaConta: string = '';
  contrapartidaValor: number = 0;
  camposDinamicos: any[] = [];

  @Input() columnsToShow: string[] = [];
  @Input() lancamentos: any[] = [];
  @Output() mostrarBalancoChange: EventEmitter<boolean> = new EventEmitter<boolean>();
  mostrarBalanco: boolean = false;

  constructor(private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Usando um evento para detectar alterações na rota
    this.router.events
      .pipe(filter(event => event instanceof NavigationEnd))
      .subscribe(() => {
        // Obtendo os dados do estado da rota
        const navigationState = this.route.snapshot?.root.firstChild?.data['state'];

        if (navigationState) {
          this.nome = navigationState.nome;
          this.descricao = navigationState.descricao;
        }
      });
  }

  gerarBalanco() {
    this.mostrarBalanco = true;

  }

  adicionarCampo() {
    const novoCampo = {
      tipo: '',
      conta: '',
      valor: 0,
      funcaoCredito: false
    };

    this.camposDinamicos.push({ ...novoCampo });
  }

  adicionarLancamento() {
    const totalCamposDinamicos = this.camposDinamicos.reduce((acc, campo) => {
      return campo.funcaoCredito ? acc + campo.valor : acc - campo.valor;
    }, 0);

    const margemErro = 0.0000001;

    if (Math.abs(this.valor - (this.contrapartidaValor + totalCamposDinamicos)) > margemErro) {
      alert('A contrapartida deve ter o mesmo valor do lançamento.');
      return;
    }

    const novoLancamento = {
      tipo: 'Débito',
      conta: this.conta,
      valor: this.valor,
      tipoLancamento: this.tipoLancamento,
      contrapartida: {
        tipo: 'Crédito',
        conta: this.contrapartidaConta,
        valor: this.contrapartidaValor,
        tipoLancamento: this .tipoLancamentoContrapartida,
      },
      camposDinamicos: [...this.camposDinamicos]
    };

    this.lancamentos.push({ ...novoLancamento });
    this.limparCampos();

    const totalDebitos = this.lancamentos.reduce((acc, lancamento) => lancamento.tipo === 'Débito' ? acc + lancamento.valor : acc, 0);
    const totalCreditos = this.lancamentos.reduce((acc, lancamento) => lancamento.tipo === 'Crédito' ? acc + lancamento.valor : acc, 0);


  }

  limparCampos() {
    this.tipoLancamento = '';
    this.conta = '';
    this.valor = 0;
    this.tipoLancamentoContrapartida = '';
    this.contrapartidaConta = '';
    this.contrapartidaValor = 0;
    this.camposDinamicos = [];
  }

  limparLancamentos(){
    this.lancamentos.pop();
  }
}

<div class="quadroLancamentos">
  <label for="tipoLancamento">Tipo de Lançamento Débito:</label>
  <select id="tipoLancamento" [(ngModel)]="tipoLancamento">
    <option *ngFor="let tipo of tiposLancamento" [value]="tipo">{{ tipo }}</option>
  </select>
  <br>
  <label for="conta">Conta:</label>
  <select id="conta" [(ngModel)]="conta">
    <option *ngFor="let conta of contas" [value]="conta">{{ conta }}</option>
  </select>
  <br>
  <label for="valor">Valor:</label>
  <input type="number" id="valor" [(ngModel)]="valor" placeholder="0.000.000,00">

  <!-- Contrapartida -->
  <div>
    <label for="tipoLancamentoContrapartida">Tipo de Lançamento Crédito:</label>
    <select id="tipoLancamentoContrapartida" [(ngModel)]="tipoLancamentoContrapartida">
      <option *ngFor="let tipo of tiposLancamento" [value]="tipo">{{ tipo }}</option>
    </select>
    <br>
    <label for="contrapartidaConta">Conta:</label>
    <select id="contrapartidaConta" [(ngModel)]="contrapartidaConta">
      <option *ngFor="let conta of contas" [value]="conta">{{ conta }}</option>
    </select>
    <br>
    <label for="contrapartidaValor">Valor:</label>
    <input type="number" id="contrapartidaValor" [(ngModel)]="contrapartidaValor" placeholder="0.000.000,00">
  </div>

  <!-- Novo campo -->
  <div *ngFor="let campo of camposDinamicos; let i = index">
    <label for="tipoLancamentoNCampo{{ i }}">Tipo de Lançamento:</label>
    <select id="tipoLancamentoNCampo{{ i }}" [(ngModel)]="campo.tipo">
      <option *ngFor="let tipo of tiposLancamento" [value]="tipo">{{ tipo }}</option>
    </select>
<br>
    <label for="nCampoConta{{ i }}">Conta:</label>
    <select id="nCampoConta{{ i }}" [(ngModel)]="campo.conta">
      <option *ngFor="let conta of contas" [value]="conta">{{ conta }}</option>
    </select>
  <br>
    <label for="nCampoValor{{ i }}">Valor:</label>
    <input type="number" id="nCampoValor{{ i }}" [(ngModel)]="campo.valor" placeholder="0.000.000,00">

    <label for="nCampoFuncao{{ i }}">Função:</label>
    <input class="checkCredito" type="checkbox" id="nCampoFuncao{{ i }}" [(ngModel)]="campo.funcaoCredito">
    <label for="nCampoFuncao{{ i }}">Crédito</label>
  </div>
  <button (click)="adicionarCampo()">Adicionar Campo</button>
  <button (click)="adicionarLancamento()">Gravar Lançamento</button>
  <button (click)="limparLancamentos()">Limpar Lançamento</button>

 <!--<button (click)="gerarBalanco()">Gerar Balanço</button>-->
</div>

<table>
  <thead>
    <tr>
      <th>Nº Lançamento</th>
      <th>Função</th>
      <th>Tipo de Lançamento</th>
      <th>Conta</th>
      <th>Valor</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let lancamento of lancamentos; let i = index">
      <td>{{ i + 1 }}</td>
      <div>
      <td>{{ lancamento.tipo }}</td>
      <td>{{ lancamento.tipoLancamento }}</td>
      <td>{{ lancamento.conta }}</td>
      <td>{{ lancamento.valor | currency }}</td>
    </div>
    <div>
      <td>{{ lancamento.contrapartida?.tipo }}</td>
      <td>{{ lancamento.contrapartida?.tipoLancamento }}</td>
      <td>{{ lancamento.contrapartida?.conta }}</td>
      <td>{{ lancamento.contrapartida?.valor | currency }}</td>
    </div>
    <ng-container *ngFor="let campo of lancamento.camposDinamicos; let j = index">
      <div>
      <td>{{ campo.funcaoCredito ? 'Crédito' : 'Débito' }}</td>
        <td>{{ campo.tipo }}</td>
        <td>{{ campo.conta }}</td>
        <td>{{ campo.valor | currency }}</td>
      </div>
      </ng-container>
    </tr>
</table>
<div *ngIf="mostrarBalanco">
  <div #tabelaB class="balanco-table" [ngClass]="{'ocultar': !mostrarBalanco}">
</div>
