import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UsuarioService } from 'src/app/Servicos/usuarios/usuario.service';
import { Usuario } from 'src/app/Usuario';

@Component({
  selector: 'app-area-user',
  templateUrl: './area-user.component.html',
  styleUrls: ['./area-user.component.css']
})
export class AreaUserComponent implements OnInit{
  usuario!:Usuario;

  constructor(private router:Router, private usuarioService: UsuarioService){}

  ngOnInit(): void {
    this.usuario = this.usuarioService.getUsuario();
  }

  Edit(){

    return this.router.navigate(['/editUser']);
  }
}
<div class="areaUser"> <!--aplicar ng for e associar o bem vindo ao apelido ou nome da pessoa respectivamente-->
  <h1>Bem vindo {{ usuario.Nome }}!</h1>
  <img src="/upload/wallpaperflare.com_wallpaper (2).jpg" alt="FotoPerfil" />
  <label>Usuário:</label>
  <p id="Nome">{{ usuario.Nome }}</p>
  <label>Email:</label>
  <p id="E-mail">{{ usuario.email }}</p>
  <label>Instituição:</label>
  <p id="Instituição">{{ usuario.instituicao }}</p>
  <label>Responsável:</label>
  <p id="Responsável">{{ usuario.resposavel }}</p>
</div>
<div class="btn-comandos-userArea">
  <button (click)="Edit()" Alt="Edição">Editar</button>
  <button Alt="Sair">Sair</button><!--Não funcional ainda-->
</div>
<div class="areaUser-task">
  <h1>Veja seus balanços:</h1>
  <table class="taskUser">
      <tr>
          <!-- Exiba os balanços aqui -->
          <td>balanço 1</td>
          <td>balanço 2</td>
          <td>balanço 3</td>
      </tr>
      <tr>
          <td>balanço 4</td>
          <td>balanço 5</td>
          <td>balanço 6</td>
      </tr>
  </table>
</div>
