import { Component, OnInit } from '@angular/core';
import { UsuarioService } from 'src/app/Servicos/usuario.service';
import { Usuario } from 'src/app/Usuario';

@Component({
  selector: 'app-edicao-user',
  templateUrl: './edicao-user.component.html',
  styleUrls: ['./edicao-user.component.css']
})
export class EdicaoUserComponent implements OnInit {
  usuario: Usuario = {
    Nome: '',
    Email: '',
    Nickname: '',
    Password: '',
    Resposavel: '',
    Instituicao: ''
  };
  editavel: boolean = false;

  constructor(private usuarioService: UsuarioService) {}

  ngOnInit(): void {
    this.usuario = this.usuarioService.getUsuario();
  }

  alterarCampo() {
    if (this.editavel) {
      this.usuarioService.atualizarUsuario(this.usuario);
      this.editavel = false;
    } else {
      this.editavel = true;
    }
  }

  ocultarCampo(){
    this.editavel = false;
  }
}
<div class="editUser">
  <h2>Edição</h2>
  <div class="editCad-container">
      <div class="btn-edit">
          <button [ngClass]="editavel ? 'ocultar-btn' : 'exibir-btn'" alt="Alterar" (click)="ocultarCampo()">Alterar</button><!-- Não implementado -->
          <button [ngClass]="editavel ? 'exibir-btn':'ocultar-btn'" alt="Salvar" (click)="alterarCampo()">Salvar</button><!-- Não implementado -->
          <button alt="Voltar">Voltar</button><!-- Não implementado -->
      </div>
      <div class="edit" ngForm="Cadastro">
          <label>Nome Completo:</label>
          <input type="text" alt="Nome" id="Nome" [(ngModel)]="usuario.Nome" [readonly]="!editavel" />
          <label>Apelido:</label>
          <input type="text" alt="Apelido" id="Nickname" [(ngModel)]="usuario.Nickname" [readonly]="!editavel" />
          <label>Digite seu e-mail:</label>
          <input type="email" alt="E-mail" id="Email" [(ngModel)]="usuario.Email" [readonly]="!editavel" />
          <label>Instituição:</label>
          <input type="text" alt="Instituição" id="Instituicao" [(ngModel)]="usuario.Instituicao" />
          <label>Responsável:</label>
          <input type="text" alt="Responsável" id="Responsavel" [(ngModel)]="usuario.Resposavel" />

      </div>
  </div>
</div>
