import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {

  ocultar = true;
  iconOcultar = true;

  constructor() { }

  ngOnInit(): void {

  const btn_menu = document.querySelector(".btn-menu") as HTMLElement;
  const side_bar = document.querySelector(".sidebar") as HTMLElement;

  btn_menu.addEventListener('click', () => {
    side_bar.classList.toggle('expand');

  this.ocultar = !this.ocultar; //Alterar o estado do texto
  this.iconOcultar = !this.iconOcultar; //Alterar o estado do icone
});
}
}
function changebtn() {
  const side_bar = document.getElementById('side_bar'); // Substitua 'side_bar' pelo ID correto
  const btn_menu = document.getElementById('btn_menu');   // Substitua 'btn_menu' pelo ID correto

  if (side_bar && btn_menu) {
    if (side_bar.classList.contains('expand')) {
      btn_menu.classList.replace('bx bx-menu', 'bx bx-menu-alt-right');
    } else {
      btn_menu.classList.replace('bx bx-menu-alt-right', 'bx bx-menu');
    }
  }
}

<nav>
  <section class="sidebar" [ngClass]="{'ocultar' : ocultar}">
  <div class="nav-header">
    <p class="logo">Área de estudos</p>
    <i class="bx bx-menu-alt-right btn-menu"></i>
    </div>
    <ul class="nav-routes">
      <li>
      <i class='bx bx-search-alt-2 busca-btn'></i>
      <input type="text" placeholder="Buscar...">
    </li>
      <li class="routes" routerLink="">
             <i class='bx bx-home'></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Início</span>
      </li>
      <li class="routes" routerLink="/lançamentos">
        <i class='bx bx-spreadsheet'></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Lançamentos</span>
      </li>
      <li class="routes" routerLink="/balanços">
        <i Class="bx bxs-bar-chart-alt-2"></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Balanços</span>
      </li>
      <li class="routes" routerLink="">
        <i class='bx bx-buildings'></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Contole</span>
      </li>
      <li class="routes" routerLink="">
        <i class='bx bx-user'></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Perfil</span>
      </li>
      <li class="routes" routerLink="/teste">
        <i class='bx bx-cog' ></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Testes desenvolvimento</span>
      </li>
      <li class="routes" routerLink="/login">
        <i class='bx bx-cog' ></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Login</span>
      </li>
      <li class="routes" routerLink="/areaUser">
        <i class='bx bx-cog' ></i>
        <span class="text" [ngClass]="{'hidden-text': ocultar}">Usuário</span>
      </li>
    </ul>
    <div class="theme-wrapper">
      <i class='bx bxs-moon theme-icon'></i>
      <p>Tema escuro</p>
      <div class="theme-btn">
        <span class="theme-ball"></span>
      </div>
    </div>
  </section>

<nav>
