import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent implements OnInit{

  @Input() lancamentos: any[] = [];
  @Input() columnsToShow: string[] = [];
  @Input() tipoLancamento: string = '';

  constructor(){}

  ngOnInit(): void { }

}
<form id="novoBalanco">
  <div #btnNovoDiv class="btn-novo">
  <label>Criar novo balanço</label>
  <br>
  <button #btnNovo class="btn-novo" (click)="nBalanco()"><i class='bx bx-plus-circle'></i></button>
  </div>

  <div #criarBalanco class="criarBalanco ocultar">
  <label for="nome">Nome do balanço:</label>
  <br>
  <input  type="text" id="name" placeholder="Digite seu texto" name="name" required>
  <br>
  <br>
  <label for="description">Descrição:</label>
  <br>
  <textarea id="description" name="description" rows="6" cols="50" placeholder="Digite a descrição do balanço"></textarea>
  <br>
  <button class="novo-balanco" (click)="newBalanco()">Criar Balanço</button>
  </div>
</form>
